import asyncio
import json
import uuid
from aiokafka import AIOKafkaProducer, AIOKafkaConsumer

# KAFKA_BOOTSTRAP_SERVERS = "kafka:9092"
KAFKA_BOOTSTRAP_SERVERS = "localhost:29092"
KAFKA_REQUEST_TOPIC = "descRequests"
KAFKA_RESPONSE_TOPIC = "descResponses"


async def main():
    """
    Sends a single image description request to Kafka and waits for the response.
    """
    request_id = str(uuid.uuid4())
    print(f"üöÄ Starting Kafka test with request_id: {request_id}")

    producer = AIOKafkaProducer(bootstrap_servers=KAFKA_BOOTSTRAP_SERVERS)
    consumer = AIOKafkaConsumer(
        KAFKA_RESPONSE_TOPIC,
        bootstrap_servers=KAFKA_BOOTSTRAP_SERVERS,
        group_id=f"test-consumer-{request_id}",
        auto_offset_reset="latest"
    )
    await producer.start()
    await consumer.start()
    print("‚úÖ Kafka producer and consumer connected.")

    try:
        image_b64 = "data:image/png;base64,"
        payload = {"request_id": request_id, "image_base64": image_b64}
        message = json.dumps(payload).encode("utf-8")
        await producer.send_and_wait(KAFKA_REQUEST_TOPIC, message)
        print(f"üì¨ Message sent to topic '{KAFKA_REQUEST_TOPIC}'")
        print(f"üëÇ Listening for response on topic '{KAFKA_RESPONSE_TOPIC}'...")
        response_received = False
        async for msg in consumer:
            response_data = json.loads(msg.value.decode("utf-8"))
            if response_data.get("request_id") == request_id:
                print("\nüéâ SUCCESS! Full round-trip complete.")
                print(f"   Request ID:  {response_data.get('request_id')}")
                print(f"   Description: '{response_data.get('description')}'")
                response_received = True
                break
            else:
                print(f"   (Skipping message with unrelated request_id: {response_data.get('request_id')})")
        if not response_received:
            print("\n‚ùå FAILED: Consumer finished without receiving the expected response.")
    except Exception as e:
        print(f"\n‚ùå An error occurred during the test run: {e}")
    finally:
        print("\nüßπ Shutting down Kafka clients...")
        await producer.stop()
        await consumer.stop()
        print("üõë Test finished.")
if __name__ == "__main__":
    try:
        asyncio.run(asyncio.wait_for(main(), timeout=60.0))
    except asyncio.TimeoutError:
        print("\n‚ùå FAILED: The entire test script timed out after 60 seconds.")
    except Exception as _:  # noqa: F841
        print(f"\n‚ùå FAILED: Could not connect to Kafka at '{KAFKA_BOOTSTRAP_SERVERS}'. Is it running?")
